// Preparar dados das conversas para analise de reflection
const agente = $('Loop - Processar Agentes').first().json;
const conversas = $input.all().map(item => item.json);

if (conversas.length === 0) {
  return [{ json: { erro: true, motivo: 'Sem conversas para analisar' } }];
}

// Agrupar informacoes das conversas
const conversasFormatadas = conversas.map((c, idx) => {
  return {
    id: c.conversation_id,
    canal: c.channel,
    outcome: c.outcome || 'indefinido',
    qa_score: c.qa_score || 0,
    mensagens_total: c.mensagens_total,
    status: c.conversation_status
  };
});

// Estatisticas basicas
const totalConversas = conversas.length;
const mediaQaScore = conversas.reduce((sum, c) => sum + (c.qa_score || 0), 0) / totalConversas;
const conversasAgendadas = conversas.filter(c => c.outcome === 'agendado' || c.outcome === 'scheduled').length;
const conversasPerdidas = conversas.filter(c => c.outcome === 'perdido' || c.outcome === 'lost').length;
const conversasEmAndamento = conversas.filter(c => c.outcome === 'em_andamento' || c.outcome === 'in_progress' || !c.outcome).length;

return [{
  json: {
    agente: {
      id: agente.agent_version_id,
      nome: agente.agent_name,
      cliente: agente.cliente_nome,
      versao: agente.version
    },
    system_prompt_atual: agente.system_prompt,
    estatisticas: {
      total_conversas: totalConversas,
      media_qa_score: mediaQaScore.toFixed(2),
      conversas_agendadas: conversasAgendadas,
      conversas_perdidas: conversasPerdidas,
      conversas_em_andamento: conversasEmAndamento,
      taxa_conversao: ((conversasAgendadas / totalConversas) * 100).toFixed(1) + '%'
    },
    conversas: conversasFormatadas,
    ids_conversas: conversas.map(c => c.conversation_id)
  }
}];

// Consolidar mensagens para analise
const dadosReflection = $('Preparar Dados para Reflection').first().json;
const mensagensRaw = $input.all().map(item => item.json);

// Formatar historico completo
const historicoFormatado = mensagensRaw.map((msg, idx) => {
  const timestamp = new Date(msg.created_at).toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit' });
  return "[" + timestamp + "] " + msg.sender + ": " + msg.message_text;
}).join('\n');

// Separar por outcome
const mensagensPorOutcome = {
  scheduled: [],
  lost: [],
  warmed: [],
  in_progress: []
};

mensagensRaw.forEach(msg => {
  const outcome = msg.outcome || 'in_progress';
  if (mensagensPorOutcome[outcome]) {
    mensagensPorOutcome[outcome].push(msg);
  } else {
    mensagensPorOutcome.in_progress.push(msg);
  }
});

return [{
  json: {
    ...dadosReflection,
    mensagens: {
      total: mensagensRaw.length,
      historico_completo: historicoFormatado,
      por_outcome: {
        scheduled: mensagensPorOutcome.scheduled.length,
        lost: mensagensPorOutcome.lost.length,
        warmed: mensagensPorOutcome.warmed.length,
        in_progress: mensagensPorOutcome.in_progress.length
      }
    },
    amostra_conversas: historicoFormatado.substring(0, 10000)
  }
}];

