// Processar resposta da analise de reflection
const dadosReflection = $('Consolidar Mensagens').first().json;
const respostaIA = $input.first().json;

// Funcoes de parse
function cleanFences(s) {
  if (typeof s !== 'string') s = String(s ?? '');
  let r = s.trim();
  r = r.replace(/^\s*```[a-z]*\s*/i, '');
  r = r.replace(/\s*```$/, '');
  return r;
}

function findBalancedEnd(s, start) {
  let depth = 0, inStr = false, esc = false;
  for (let i = start; i < s.length; i++) {
    const ch = s[i];
    if (inStr) {
      if (esc) { esc = false; }
      else if (ch === '\\') { esc = true; }
      else if (ch === '"') { inStr = false; }
    } else {
      if (ch === '"') inStr = true;
      else if (ch === '{') depth++;
      else if (ch === '}') { depth--; if (depth === 0) return i; }
    }
  }
  return -1;
}

function tryParse(str) {
  try { return JSON.parse(str); } catch {}
  const raw = cleanFences(str);
  try { return JSON.parse(raw); } catch {}
  const start = raw.indexOf('{');
  const end = start >= 0 ? findBalancedEnd(raw, start) : -1;
  if (start >= 0 && end >= 0) {
    const jsonStr = raw.slice(start, end + 1);
    try { return JSON.parse(jsonStr); } catch {}
  }
  return null;
}

// Parse da resposta
let reflection;
const rawVal = respostaIA.text ?? respostaIA.response ?? respostaIA.output;
const outputStr = typeof rawVal === 'string' ? rawVal : String(rawVal ?? '');
reflection = typeof rawVal === 'object' && rawVal ? rawVal : tryParse(outputStr);

if (!reflection) {
  return [{
    json: {
      agente: dadosReflection.agente,
      erro_parse: true,
      motivo: 'Resposta do Reflection Analyst incompleta ou JSON invalido.',
      raw_snippet: cleanFences(outputStr).slice(0, 500)
    }
  }];
}

// Extrair scores dos criterios
const criterios = reflection.criterios || {};
const scoreCompletude = criterios.completude?.score || 0;
const scoreProfundidade = criterios.profundidade?.score || 0;
const scoreTom = criterios.tom_personalidade?.score || 0;
const scoreEscopo = criterios.escopo_relevancia?.score || 0;
const scoreOportunidades = criterios.oportunidades_perdidas?.score || 0;

// Calcular score ponderado
const scorePonderado = (
  (scoreCompletude * 0.20) +
  (scoreProfundidade * 0.25) +
  (scoreTom * 0.15) +
  (scoreEscopo * 0.20) +
  (scoreOportunidades * 0.20)
);

// Determinar action baseado no score
let action = 'none';
let classificacao = 'EXCELENTE';

if (scorePonderado >= 4.0) {
  action = 'none';
  classificacao = 'EXCELENTE';
} else if (scorePonderado >= 3.0) {
  action = 'suggestion';
  classificacao = scorePonderado >= 3.5 ? 'BOM' : 'ADEQUADO';
} else if (scorePonderado >= 2.0) {
  action = 'auto_update';
  classificacao = 'PRECISA_MELHORIA';
} else {
  action = 'escalate';
  classificacao = 'CRITICO';
}

return [{
  json: {
    agente: dadosReflection.agente,
    reflection: reflection,
    scores: {
      completude: scoreCompletude,
      profundidade: scoreProfundidade,
      tom: scoreTom,
      escopo: scoreEscopo,
      oportunidades: scoreOportunidades,
      ponderado: parseFloat(scorePonderado.toFixed(2))
    },
    classificacao: classificacao,
    action: reflection.action || action,
    pontos_fortes: reflection.pontos_fortes || [],
    pontos_fracos: reflection.pontos_fracos || [],
    sugestoes_prompt: reflection.sugestoes_prompt || [],
    urgencia: reflection.urgencia || (action === 'escalate' ? 'CRITICA' : 'NENHUMA'),
    resumo: reflection.resumo_executivo || 'Analise de reflection concluida',
    metricas: dadosReflection.estatisticas
  }
}];

