// Formatar resposta do fluxo de caixa
// Dados vêm do Merge node agora
const items = $input.all();

// Separar os dados - fluxo diário vem do primeiro input, totais do segundo
const fluxoDiario = items.filter(i => i.json.data_vencimento);
const totaisItem = items.find(i => i.json.total_entradas_previstas !== undefined);
const totais = totaisItem ? totaisItem.json : {
  total_entradas_previstas: 0,
  total_saidas_previstas: 0,
  saldo_projetado: 0
};

const formatarValor = (valor) => new Intl.NumberFormat('pt-BR', {
  style: 'currency',
  currency: 'BRL'
}).format(valor || 0);

const formatarData = (dataStr) => {
  if (!dataStr) return '-';
  const data = new Date(dataStr + 'T00:00:00');
  return data.toLocaleDateString('pt-BR', {day: '2-digit', month: '2-digit'});
};

// Agrupar por semana
const semanas = {};
fluxoDiario.forEach(item => {
  const d = item.json;
  const data = new Date(d.data_vencimento + 'T00:00:00');
  const semana = Math.ceil((data.getDate()) / 7);
  const chave = `Semana ${semana}`;

  if (!semanas[chave]) {
    semanas[chave] = { entradas: 0, saidas: 0 };
  }
  semanas[chave].entradas += parseFloat(d.entradas) || 0;
  semanas[chave].saidas += parseFloat(d.saidas) || 0;
});

const entradasPrevistas = parseFloat(totais.total_entradas_previstas) || 0;
const saidasPrevistas = parseFloat(totais.total_saidas_previstas) || 0;
const saldoProjetado = parseFloat(totais.saldo_projetado) || 0;

return {
  json: {
    resumo: {
      periodo: 'Próximos 30 dias',
      total_entradas: formatarValor(entradasPrevistas),
      total_saidas: formatarValor(saidasPrevistas),
      saldo_projetado: formatarValor(saldoProjetado),
      situacao: saldoProjetado >= 0 ? 'POSITIVO' : 'NEGATIVO'
    },
    por_semana: Object.entries(semanas).map(([semana, valores]) => ({
      semana,
      entradas: formatarValor(valores.entradas),
      saidas: formatarValor(valores.saidas),
      saldo: formatarValor(valores.entradas - valores.saidas)
    })),
    detalhes_diarios: fluxoDiario.slice(0, 10).map(item => {
      const d = item.json;
      return {
        data: formatarData(d.data_vencimento),
        entradas: formatarValor(d.entradas),
        saidas: formatarValor(d.saidas),
        saldo_acumulado: formatarValor(d.saldo_acumulado)
      };
    }),
    alerta: saldoProjetado < 0 ? `ATENÇÃO: Saldo projetado negativo de ${formatarValor(Math.abs(saldoProjetado))}` : null
  }
};
