// 1. Pega todas as mensagens disponíveis na fila
const todasMensagens = $('Buscar mensagens').all();

// 2. CRÍTICO: Check de Fila Vazia (Race Condition)
// Se a fila estiver vazia, significa que o workflow "vencedor" já acordou, 
// processou e limpou a fila enquanto este workflow estava esperando.
if (!todasMensagens || todasMensagens.length === 0) {
  return []; // Para o workflow imediatamente. Este workflow perdeu.
}

// 3. Ordenar as mensagens por timestamp (do mais recente para o mais antigo)
// Adicionamos um desempate pelo ID da tabela caso dois timestamps sejam idênticos.
const filaOrdenada = todasMensagens.sort((a, b) => {
  const tA = new Date(a.json.timestamp).getTime();
  const tB = new Date(b.json.timestamp).getTime();

  // Se timestamps forem diferentes, ordena por tempo
  if (tA !== tB) {
    return tA - tB;
  }

  // Se timestamps forem iguais (raro, mas possível), desempata pelo ID
  return (a.json.id || 0) - (b.json.id || 0);
});

// 4. Identificar a mensagem "Vencedora" (a última da fila)
const mensagemVencedora = filaOrdenada[filaOrdenada.length - 1];
const idVencedor = mensagemVencedora.json.id_mensagem;

// 5. Pegar o ID da mensagem que este workflow atual está processando
const idWorkflowAtual = $('Info').first().json.mensagem_id;

// 6. Comparar: Eu sou o dono da última mensagem?
if (idVencedor !== idWorkflowAtual) {
  // NÃO sou o vencedor. A última mensagem pertence a outro workflow.
  // Retorno array vazio para parar este fluxo imediatamente.
  return []; 
}

// 7. SIM, sou o vencedor!
// Retorno TODA a fila ordenada para que a IA processe as mensagens agrupadas.
return filaOrdenada;

