  // Prioriza o canal da mensagem ATUAL, não a origem do lead
  (() => {
    const messageType = $json.body?.message?.type;

    // Type 2 = SMS - responde por SMS (usa "whatsapp" que no seu workflow envia SMS)
    if (messageType === 2) return "whatsapp";

    // Type 15 = Instagram DM - pode responder por Instagram
    if (messageType === 15) return "instagram";

    // Se não conseguir identificar pelo type, verifica a origem
    // mas só usa Instagram se o type não for SMS
    const origin = $json.body?.contact?.attributionSource?.medium;
    if (origin === "instagram" && messageType !== 2 && messageType !== 20) return "instagram";

    // Default: SMS/WhatsApp (mais seguro)
    return "whatsapp";
  })().trim()
}}

