if (!agente_ia) {
  // Verifica tags específicas
  if (tags.includes('closer')) {
    agente_ia = 'closer';
  } else if (tags.includes('followuper')) {
    agente_ia = 'followuper';
  } else if (objetivo_do_lead === 'consultoria') {
