if (input.agente_ia === 'social_seller_instagram' || input.source === 'instagram') {
  source = 'instagram';
}

// Extrair etiquetas (pode ser string ou array)
let etiquetas = [];
if (input.etiquetas) {
  etiquetas = typeof input.etiquetas === 'string' 
    ? input.etiquetas.split(',').map(t => t.trim()).filter(t => t) 
