const prev = $input.item.json;
const schedulingConfig = prev.scheduling_config || {};
const calendariosGHL = prev.calendarios_ghl || {};

// Mapear calendários - prioridade para GHL, fallback para config do agente
const calendarios = [];

const calSP = calendariosGHL.sao_paulo || schedulingConfig.agenda_consultorio_sao_paulo;
