const dadosAnteriores = $('1.6 Preparar Dados').item.json;
const respostaIA = $input.first().json;

// ========== FUN√á√ïES DE PARSE ==========
function cleanFences(s) {
  if (typeof s !== 'string') s = String(s ?? '');
  let r = s.trim();
  r = r.replace(/^\s*```[a-z]*\s*/i, '');
  r = r.replace(/\s*```$/, '');
  return r;
}

function findBalancedEnd(s, start) {
  let depth = 0, inStr = false, esc = false;
  for (let i = start; i < s.length; i++) {
    const ch = s[i];
    if (inStr) {
      if (esc) { esc = false; }
      else if (ch === '\\') { esc = true; }
      else if (ch === '"') { inStr = false; }
    } else {
      if (ch === '"') inStr = true;
      else if (ch === '{') depth++;
      else if (ch === '}') { depth--; if (depth === 0) return i; }
    }
  }
  return -1;
}

function tryParse(str) {
  try { return JSON.parse(str); } catch {}
  const raw = cleanFences(str);
  try { return JSON.parse(raw); } catch {}
  const start = raw.indexOf('{');
  const end = start >= 0 ? findBalancedEnd(raw, start) : -1;
  if (start >= 0 && end >= 0) {
    const jsonStr = raw.slice(start, end + 1);
    try { return JSON.parse(jsonStr); } catch {}
  }
  return null;
}

// ========== PARSE DA RESPOSTA ==========
let analise;
const rawVal = respostaIA.output ?? respostaIA.text;
const outputStr = typeof rawVal === 'string' ? rawVal : String(rawVal ?? '');
analise = typeof rawVal === 'object' && rawVal ? rawVal : tryParse(outputStr);

